	loadList : function(fCallback, sCategory, sField, sOrder, nLimit, nOffset){
		var sSql = 'SELECT * FROM ' + this._sPrefix + this._sTableList;
		switch(sCategory){
		case 'isreading' :
		case 'willread' :
		case 'hasread' :
			sSql += ' WHERE db_category = "' + sCategory + '"';
			break;
		}
		if(sField && sOrder){
			sSql += ' ORDER BY ' + sField + ' ' + sOrder; 
		}
		if(nLimit > 0){
			sSql += ' LIMIT ' + nLimit;
		}
		if(nOffset > 0){
			sSql += ' ' + nOffset;
		}
		
		var self = this;
		this._db.transaction(function(tx){
			tx.executeSql(sSql, [], fCallback, self._onError);
		});
	},
	
	loadFavoriteList : function(fCallback, sField, sOrder, nLimit, nOffset){
		var sSql = 'SELECT * FROM ' + this._sPrefix + this._sTableList;
			sSql += ' WHERE db_favorite = "on"'
		if(sField && sOrder){
			sSql += ' ORDER BY ' + sField + ' ' + sOrder; 
		}
		if(nLimit > 0){
			sSql += ' LIMIT ' + nLimit;
		}
		if(nOffset > 0){
			sSql += ' ' + nOffset;
		}
		
		var self = this;
		this._db.transaction(function(tx){
			tx.executeSql(sSql, [], fCallback, self._onError);
		});
	},
	
	loadBook : function(nIdx, fCallback){
		var sSql = 'SELECT * FROM ' + this._sPrefix + this._sTableList;
			sSql += ' WHERE db_idx = ?';
		
		var self = this;
		this._db.transaction(function(tx){
			tx.executeSql(sSql, [nIdx], function(tx, results){
                //console.log(results);
                var sCommentSql = 'SELECT * FROM ' + self._sPrefix + self._sTableComments
                    + ' WHERE db_idx = ?';
                //console.log(sInnerSql);
                tx.executeSql(sCommentSql, [nIdx], function(tx, commentResults){
                    //console.log(commentResults);
                    results.commentResults = commentResults;
                    
                    var sImageSql = 'SELECT * FROM ' + self._sPrefix + self._sTableImages
                    + ' WHERE db_idx = ?';
                    //console.log(sImageSql);
                    tx.executeSql(sImageSql, [nIdx], function(tx, imageResults){
                        //console.log(imageResults);
                        results.imageResults = imageResults;
                        fCallback(results);
                    }, self._onError); 
                }, self._onError);   
            }, self._onError);
		});
	},
	
	getCount : function(fCallback, sCategory){
		var sSql = 'SELECT COUNT(db_idx) AS count FROM ' + this._sPrefix + this._sTableList;
		if(sCategory == 'favorite'){
			sSql += ' WHERE db_favorite = "on"';
		}else if(sCategory == 'all'){
		}else{
			sSql += ' WHERE db_category = "' + sCategory + '"';
		}
		var self = this;
		this._db.transaction(function(tx){
			tx.executeSql(sSql, [], fCallback, self._onError);
		});
	},
	
